options.afterRender:
let filteredData = [];
let selectedField = "";
let selectedTimeRange = "";
let isFiltering = false;
let refreshIntervalId = null;
let debounceTimeout = null;

const keyMapping = {
    "formatted_log_timestamp": "Formatted Timestamp",
    "@timestamp": "Log Timestamp",
    "module": "Module",
    "log_level": "Log Level",
    "log_message": "Log Message",
    "payload_json": "Payload"
};

const timeRanges = [
    { label: "5m", duration: 5 * 60 * 1000 },
    { label: "10m", duration: 10 * 60 * 1000 },
    { label: "15m", duration: 15 * 60 * 1000 },
    { label: "20m", duration: 20 * 60 * 1000 },
    { label: "30m", duration: 30 * 60 * 1000 },
    { label: "1h", duration: 1 * 60 * 60 * 1000 },
    { label: "1d", duration: 24 * 60 * 60 * 1000 },
    { label: "7d", duration: 7 * 24 * 60 * 60 * 1000 },
    { label: "30d", duration: 30 * 24 * 60 * 60 * 1000 }
];

// Function to apply the time filter based on selected range
applyTimeFilter = (panelId) => {
    console.log("inside apply time filter for panel:", panelId);
    const timeFilter = document.querySelector(`#timeFilter-${panelId}`);
    selectedTimeRange = timeFilter.value;
    console.log("selected time range for panel", panelId, selectedTimeRange);
    filterData(panelId);  // Re-apply filter with time range
};

filterData = (panelId) => {
    console.log("Inside filterData for panel:", panelId);
    isFiltering = true;
    const searchValue = document.querySelector(`#searchFilter-${panelId}`).value.toLowerCase();
    const values = context.data;
    const dataArray = Array.isArray(values) ? values : Object.values(values);

    const now = new Date();
    let filteredDataByTime = dataArray;

    if (selectedTimeRange) {
        // Find the matching time range and calculate timeLimit
        const selectedRange = timeRanges.find(range => range.label === selectedTimeRange);
        if (selectedRange) {
            const timeLimit = new Date(now.getTime() - selectedRange.duration);
            filteredDataByTime = dataArray.filter(item => {
                const itemTimestamp = new Date(item['@timestamp']);
                return itemTimestamp >= timeLimit;
            });
            console.log("FilteredTimeData for panel", panelId, filteredDataByTime);
        }
    }

    if (selectedField && searchValue) {
        console.log("selected field and filtering...");
        filteredData = filteredDataByTime.filter(item => {
            const fieldValue = item[selectedField];
            return fieldValue && fieldValue.toString().toLowerCase().includes(searchValue);
        });
    } else if (searchValue) {
        console.log("only search filtering...");
        filteredData = filteredDataByTime.filter(item =>
            Object.values(item).some(value =>
                value && value.toString().toLowerCase().includes(searchValue)
            )
        );
    } else {
        console.log("assigning filter data by time for panel", panelId);
        filteredData = filteredDataByTime;
    }
    console.log("updating UI for panel", panelId, filteredData);
    updateCardUI(panelId, filteredData);
};

// Function to handle field selection, updates the selected field
onFieldSelect = (panelId) => {
    const fieldSelect = document.querySelector(`#fieldSelect-${panelId}`);
    selectedField = fieldSelect.value;
    filterData(panelId);
};

// Event listener for search input with debounce
document.querySelectorAll("[id^='searchFilter-']").forEach(inputElement => {
    inputElement.addEventListener("input", (event) => {
        const panelId = event.target.getAttribute('data-panel');
        clearTimeout(debounceTimeout);
        debounceTimeout = setTimeout(() => {
            if (!event.target.value && !selectedTimeRange) {
                isFiltering = false;
            } else {
                filterData(panelId);
            }
        }, 300);
    });
});

updateCardUI = (panelId, filteredData) => {
    const accordionContainer = document.querySelector(`#accordionContainer[data-panel="${panelId}"]`);
    accordionContainer.innerHTML = '';
    console.log(accordionContainer);
    filteredData.forEach(item => {
        const accordionItem = document.createElement("div");
        accordionItem.classList.add("accordion-item");
        const timestamp = new Date(item["@timestamp"]);
        const accordionHeader = document.createElement("button");
        accordionHeader.classList.add("accordion-header");
        accordionHeader.innerHTML = `
            <p style="display:inline; padding:0px 10px">
                ${item["@timestamp"]} IST &nbsp;||&nbsp; ${item["formatted_log_timestamp"]} UTC
            </p>
        `;
        accordionHeader.onclick = () => toggleAccordion(accordionHeader);
        console.log("inside update UI toggling");
        const accordionContent = document.createElement("div");
        accordionContent.classList.add("accordion-content");

        let tableHTML = "<table class='accordion-table'>";
        for (const [key, value] of Object.entries(item)) {
            if (key === "id") continue;  // Skip the "id" field
            // Check if the key exists in the mapping, otherwise replace underscores with spaces
            let displayKey = keyMapping[key] || key.replace(/_/g, " ");
            tableHTML += `
                <tr>
                    <td><strong>${capitalizeFirstLetter(displayKey)}:</strong></td>
                    <td>${value}</td>
                </tr>
            `;
        }
        tableHTML += "</table>";
        accordionContent.innerHTML = tableHTML;
        accordionContent.style.display = "none";
        accordionItem.appendChild(accordionHeader);
        accordionItem.appendChild(accordionContent);
        accordionContainer.appendChild(accordionItem);
    });
};

function capitalizeFirstLetter(str) {
    return str.charAt(0).toUpperCase() + str.slice(1);
}

generateReport = (panelId) => {
    console.log("Generating report for panel:", panelId);
    const dataArray = filteredData.length > 0 ? filteredData : context.data;

    const accordionContainer = document.querySelector(`.accordion-container[data-panel="${panelId}"]`);
    if (!accordionContainer) {
        console.error(`Accordion container with data-panel="${panelId}" not found.`);
        return;
    }

    const firstAccordionContent = accordionContainer.querySelector(".accordion-content");
    if (!firstAccordionContent) {
        console.error("Accordion content not found.");
        return;
    }

    firstAccordionContent.style.display = "block";

    const headers = Array.from(firstAccordionContent.querySelectorAll("tr")).map(row => {
        const headerCell = row.querySelector("td strong");
        return headerCell ? headerCell.textContent.replace(":", "").trim() : "";
    }).filter(header => header);

    firstAccordionContent.style.display = "none";

    let csvContent = headers.join(",") + "\n";

    dataArray.forEach(item => {
        const rowData = headers.map(header => {
            const fieldKey = Object.keys(item).find(key => key.replace(/_/g, " ").toLowerCase() === header.toLowerCase());
            return item[fieldKey] || "";
        });
        csvContent += rowData.join(",") + "\n";
    });

    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    const url = URL.createObjectURL(blob);
    link.setAttribute("href", url);
    link.setAttribute("download", `report_${panelId}.csv`);

    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
};

toggleAccordion = (element) => {
    console.log("Toggling accordion for element", element);
    const accordionContent = element.nextElementSibling;
    if (accordionContent.style.display === "block") {
        accordionContent.style.display = "none";
    } else {
        const allContents = document.querySelectorAll(".accordion-content");
        allContents.forEach(content => content.style.display = "none");
        accordionContent.style.display = "block";
    }
};

document.addEventListener("DOMContentLoaded", () => {
    const allHeaders = document.querySelectorAll(".accordion-header");
    allHeaders.forEach(header => {
        header.addEventListener("click", () => toggleAccordion(header));
    });
});

options.styles:
/* Container to align filter and button side by side */
.filter-container {
    display: flex;
    align-items: center;
    gap: 7px; /* Add space between the elements */
    flex-wrap: wrap; /* Ensure elements wrap on smaller screens */
    margin-bottom: 10px; /* Space between the filters and the cards */
}

/* Style for the input field and dropdown */
label {
    font-size: 12px;
    margin-bottom: 2px;
    color: #333;
}

select, input[type="text"] {
    padding: 6px 8px; /* Reduced padding */
    border: 1px solid #ddd;
    border-radius: 5px;
    font-size: 14px;
    max-width: 130px; /* Set maximum width for better alignment */
    width: 100%;
    box-sizing: border-box;
}

/* Style for the input field */
input[type="text"] {
    transition: border-color 0.3s ease;
}

/* Focus state for input and dropdown */
select:focus, input[type="text"]:focus {
    border-color: #007bff;
    outline: none;
}

/* Style for the download button */
.download-button {
    padding: 3px 10px; /* Reduced padding */
    background-color: #007bff;
    color: white;
    font-size: 12px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 6px; /* Space between icon and text */
    transition: background-color 0.3s ease;
}

/* Hover effect for the download button */
.download-button:hover {
    background-color: #0056b3;
}

/* Style for the download icon */
.download-icon {
    font-size: 18px;
}

/* Style for the card container */
.card-container {
    display: flex;
    flex-wrap: wrap;
    gap: 10px;
    justify-content: flex-start;
}

/* Style for each log card */
.log-card {
    border: 1px solid #ddd;
    padding: 10px;
    margin: 10px;
    width: 300px;
    background: #f9f9f9;
    border-radius: 8px;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
}

/* Hover effect for cards */
.log-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

/* Text styling inside the log cards */
.log-card p {
    font-size: 14px;
    color: #555;
    margin: 5px 0;
}

/* Responsive layout for smaller screens */
@media (max-width: 768px) {
    .filter-container {
        flex-direction: column;
        align-items: flex-start;
    }

    select, input[type="text"], .download-button {
        width: 100%;
        max-width: 300px;
    }

    .log-card {
        width: 100%;
        max-width: 90%;
    }
}

/* Accordion container */
.accordion-container {
  margin-top: 10px;
}

.accordion-item {
  margin-bottom: 6px;
}

.accordion-header {
  background-color: #0073e6;
  color: white;
  cursor: pointer;
  padding: 3px;
  width: 100%;
  border: none;
  text-align: left;
  outline: none;
}

.accordion-content {
  display: none;
  padding: 10px;
  background-color: #43473e;
}

.accordion-content {
  display: none;
  padding: 0;
  background-color: #f9f9f9;
}

/* Accordion Table */
.accordion-table {
  width: 100%;
  border-collapse: collapse;
  table-layout: fixed; /* Fixed layout to control column width */
  background-color: #f2f2f2; /* Set a light background color for the table */
}

.accordion-table td {
  padding: 8px 12px;
  border: 1px solid #ddd;
  font-size: 14px;
  color: #333;
  word-wrap: break-word; /* Ensures that long text wraps inside the cell */
  overflow-wrap: break-word; /* Adds additional support for text wrapping */
}

/* Set column width ratio: 30% for the first column, 70% for the second column */
.accordion-table td:first-child {
  width: 25%; /* First column width */
}

.accordion-table td:nth-child(2) {
  width: 75%; /* Second column width */
}

/* Row Styling */
.accordion-table tr:nth-child(even) {
  background-color: #e9e9e9; /* Lighter background for even rows */
}

.accordion-table tr:nth-child(odd) {
  background-color: #f2f2f2; /* Slightly darker background for odd rows */
}

/* Style for accordion content that is open */
.accordion-content.open {
  display: block;
  background-color: #fafafa; /* Light background for open content */
}
